function about_us_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('about_us', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: about_us_before_delete
	if(function_exists('about_us_before_delete')) {
		$args = [];
		if(!about_us_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `about_us` WHERE `id`='{$selected_id}'", $eo);

	// hook: about_us_after_delete
	if(function_exists('about_us_after_delete')) {
		$args = [];
		about_us_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='about_us' AND `pkValue`='{$selected_id}'", $eo);
}

