		'a3_content_1' => Request::val('a3_content_1', ''),
		'a3_head_2' => Request::val('a3_head_2', ''),
		'a3_content_2' => Request::val('a3_content_2', ''),
		'a3_head_3' => Request::val('a3_head_3', ''),
		'a3_content_3' => Request::val('a3_content_3', ''),
	];

	// get existing values
	$old_data = getRecord('about_us', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: about_us_before_update
	if(function_exists('about_us_before_update')) {
		$args = ['old_data' => $old_data];
		if(!about_us_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'about_us', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="about_us_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('about_us', $data['selectedID'], calculated_fields()['about_us']);

	// hook: about_us_after_update
	if(function_exists('about_us_after_update')) {
		$res = sql("SELECT * FROM `about_us` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!about_us_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='about_us' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

