	if(!check_record_permission('system_setting', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: system_setting_before_delete
	if(function_exists('system_setting_before_delete')) {
		$args = [];
		if(!system_setting_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `system_setting` WHERE `id`='{$selected_id}'", $eo);

	// hook: system_setting_after_delete
	if(function_exists('system_setting_after_delete')) {
		$args = [];
		system_setting_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='system_setting' AND `pkValue`='{$selected_id}'", $eo);
}

