	update_calc_fields('system_setting', $data['selectedID'], calculated_fields()['system_setting']);

	// hook: system_setting_after_update
	if(function_exists('system_setting_after_update')) {
		$res = sql("SELECT * FROM `system_setting` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
