		} else {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" onclick="return confirm(\'' . $Translation['are you sure?'] . '\');" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', ($ShowCancel ? '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>' : ''), $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#point_name').replaceWith('<div class=\"form-control-static\" id=\"point_name\">' + (jQuery('#point_name').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#location').replaceWith('');\n";
		$jsReadOnly .= "\tjQuery('#location, #location-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#licence').replaceWith('<div class=\"form-control-static\" id=\"licence\">' + (jQuery('#licence').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#licence, #licence-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#packages_accepted').replaceWith('<div class=\"form-control-static\" id=\"packages_accepted\">' + (jQuery('#packages_accepted').val() || '') + '</div>'); jQuery('#packages_accepted-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('#s2id_packages_accepted').remove();\n";
		$jsReadOnly .= "\tjQuery('#operating_licence').replaceWith('<div class=\"form-control-static\" id=\"operating_licence\">' + (jQuery('#operating_licence').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#operating_licence, #operating_licence-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#station_email').replaceWith('<div class=\"form-control-static\" id=\"station_email\">' + (jQuery('#station_email').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#station_email, #station_email-edit-link').hide();\n";
		$jsReadOnly .= "\tjQuery('#station_photo').replaceWith('<div class=\"form-control-static\" id=\"station_photo\">' + (jQuery('#station_photo').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#station_contact').replaceWith('<div class=\"form-control-static\" id=\"station_contact\">' + (jQuery('#station_contact').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#agent_incharge').replaceWith('<div class=\"form-control-static\" id=\"agent_incharge\">' + (jQuery('#agent_incharge').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#agent_id').replaceWith('<div class=\"form-control-static\" id=\"agent_id\">' + (jQuery('#agent_id').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(packages_accepted)%%>', $combo_packages_accepted->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(packages_accepted)%%>', $combo_packages_accepted->SelectedData, $templateCode);
	$templateCode = str_replace('<%%COMBO(station_reg_date)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_station_reg_date->GetHTML(true) . '</div>' : $combo_station_reg_date->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(station_reg_date)%%>', $combo_station_reg_date->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array();
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !$_REQUEST['Embedded']*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(point_name)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(location)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(licence)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="licence" id="licence" data-filetypes="txt|doc|docx|docm|odt|pdf|rtf" data-maxsize="2048000" accept=".txt,.doc,.docx,.docm,.odt,.pdf,.rtf">'), $templateCode);
	if($AllowUpdate && $row['licence'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(licence)%%>', '<br><input type="checkbox" name="licence_remove" id="licence_remove" value="1"> <label for="licence_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(licence)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(packages_accepted)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(operating_licence)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="operating_licence" id="operating_licence" data-filetypes="txt|doc|docx|docm|odt|pdf|rtf" data-maxsize="102400" accept=".txt,.doc,.docx,.docm,.odt,.pdf,.rtf">'), $templateCode);
	if($AllowUpdate && $row['operating_licence'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(operating_licence)%%>', '<br><input type="checkbox" name="operating_licence_remove" id="operating_licence_remove" value="1"> <label for="operating_licence_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(operating_licence)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(station_email)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(station_photo)%%>', ($noUploads ? '' : "<div>{$Translation['upload image']}</div>" . '<i class="glyphicon glyphicon-remove text-danger clear-upload hidden"></i> <input type="file" name="station_photo" id="station_photo" data-filetypes="jpg|jpeg|gif|png" data-maxsize="3072000" accept=".jpg,.jpeg,.gif,.png">'), $templateCode);
	if($AllowUpdate && $row['station_photo'] != '') {
		$templateCode = str_replace('<%%REMOVEFILE(station_photo)%%>', '<br><input type="checkbox" name="station_photo_remove" id="station_photo_remove" value="1"> <label for="station_photo_remove" style="color: red; font-weight: bold;">'.$Translation['remove image'].'</label>', $templateCode);
	} else {
		$templateCode = str_replace('<%%REMOVEFILE(station_photo)%%>', '', $templateCode);
	}
	$templateCode = str_replace('<%%UPLOADFILE(station_contact)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(agent_incharge)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(agent_id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(station_reg_date)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(point_name)%%>', safe_html($urow['point_name']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(point_name)%%>', html_attr($row['point_name']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(point_name)%%>', urlencode($urow['point_name']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(location)%%>', safe_html($urow['location']), $templateCode);
		} else {
			$templateCode = str_replace('<%%VALUE(location)%%>', safe_html($urow['location'], true), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(location)%%>', urlencode($urow['location']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(licence)%%>', safe_html($urow['licence']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(licence)%%>', html_attr($row['licence']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(licence)%%>', urlencode($urow['licence']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(packages_accepted)%%>', safe_html($urow['packages_accepted']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(packages_accepted)%%>', html_attr($row['packages_accepted']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(packages_accepted)%%>', urlencode($urow['packages_accepted']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(operating_licence)%%>', safe_html($urow['operating_licence']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(operating_licence)%%>', html_attr($row['operating_licence']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(operating_licence)%%>', urlencode($urow['operating_licence']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(station_email)%%>', safe_html($urow['station_email']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(station_email)%%>', html_attr($row['station_email']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(station_email)%%>', urlencode($urow['station_email']), $templateCode);
		$row['station_photo'] = ($row['station_photo'] != '' ? $row['station_photo'] : 'blank.gif');
		if( $dvprint) $templateCode = str_replace('<%%VALUE(station_photo)%%>', safe_html($urow['station_photo']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(station_photo)%%>', html_attr($row['station_photo']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(station_photo)%%>', urlencode($urow['station_photo']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(station_contact)%%>', safe_html($urow['station_contact']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(station_contact)%%>', html_attr($row['station_contact']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(station_contact)%%>', urlencode($urow['station_contact']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(agent_incharge)%%>', safe_html($urow['agent_incharge']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(agent_incharge)%%>', html_attr($row['agent_incharge']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agent_incharge)%%>', urlencode($urow['agent_incharge']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(agent_id)%%>', safe_html($urow['agent_id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(agent_id)%%>', html_attr($row['agent_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agent_id)%%>', urlencode($urow['agent_id']), $templateCode);
		$templateCode = str_replace('<%%VALUE(station_reg_date)%%>', @date('m/d/Y', @strtotime(html_attr($row['station_reg_date']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(station_reg_date)%%>', urlencode(@date('m/d/Y', @strtotime(html_attr($urow['station_reg_date'])))), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(point_name)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(point_name)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(location)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(location)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%GOOGLEMAP(location)%%>', '', $templateCode);
		$templateCode = str_replace('<%%GOOGLEMAPTHUMB(location)%%>', 'blank.gif', $templateCode);
		$templateCode = str_replace('<%%VALUE(licence)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(licence)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(packages_accepted)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(packages_accepted)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(operating_licence)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(operating_licence)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(station_email)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(station_email)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(station_photo)%%>', 'blank.gif', $templateCode);
		$templateCode = str_replace('<%%VALUE(station_contact)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(station_contact)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(agent_incharge)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agent_incharge)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(agent_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agent_id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(station_reg_date)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(station_reg_date)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('licenceEdit')) { document.getElementById('licenceEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('licenceEditLink')) { document.getElementById('licenceEditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('operating_licenceEdit')) { document.getElementById('operating_licenceEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('operating_licenceEditLink')) { document.getElementById('operating_licenceEditLink').style.display='none'; }";
			$templateCode.="\n\tif(document.getElementById('station_emailEdit')) { document.getElementById('station_emailEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('station_emailEditLink')) { document.getElementById('station_emailEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('drop_off');
	if($selected_id) {
		$jdata = get_joined_record('drop_off', $selected_id);
		if($jdata === false) $jdata = get_defaults('drop_off');
		$rdata = $row;
	}
	$templateCode .= loadView('drop_off-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: drop_off_dv
	if(function_exists('drop_off_dv')) {
		$args=[];
		drop_off_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}