function how_to_agent_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('how_to_agent', $selected_id, 'edit')) return false;

	$data = [
		'how_to_1' => Request::val('how_to_1', ''),
		'how_to_2' => Request::val('how_to_2', ''),
		'how_to_3' => Request::val('how_to_3', ''),
		'how_to_4' => Request::val('how_to_4', ''),
		'how_to_5' => Request::val('how_to_5', ''),
		'how_to_6' => Request::val('how_to_6', ''),
		'how_to_7' => Request::val('how_to_7', ''),
		'how_to_8' => Request::val('how_to_8', ''),
		'how_to_9' => Request::val('how_to_9', ''),
		'how_to_10' => Request::val('how_to_10', ''),
	];

	// get existing values
	$old_data = getRecord('how_to_agent', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: how_to_agent_before_update
	if(function_exists('how_to_agent_before_update')) {
		$args = ['old_data' => $old_data];
		if(!how_to_agent_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'how_to_agent', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="how_to_agent_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('how_to_agent', $data['selectedID'], calculated_fields()['how_to_agent']);

	// hook: how_to_agent_after_update
	if(function_exists('how_to_agent_after_update')) {
		$res = sql("SELECT * FROM `how_to_agent` WHERE `id`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!how_to_agent_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='how_to_agent' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

