function item_rating_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('item_rating');
	if(!$arrPerm['insert']) return false;

	$data = [
		'rater_name' => Request::lookup('userId'),
	];


	// hook: item_rating_before_insert
	if(function_exists('item_rating_before_insert')) {
		$args = [];
		if(!item_rating_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('item_rating', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// automatic userId if passed as filterer
	if($_REQUEST['filterer_userId']) {
		sql("UPDATE `item_rating` SET `userId`='" . makeSafe($_REQUEST['filterer_userId']) . "' WHERE `ratingId`='" . makeSafe($recID, false) . "'", $eo);
	}

	update_calc_fields('item_rating', $recID, calculated_fields()['item_rating']);

	// hook: item_rating_after_insert
	if(function_exists('item_rating_after_insert')) {
		$res = sql("SELECT * FROM `item_rating` WHERE `ratingId`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=[];
		if(!item_rating_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('item_rating', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) item_rating_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

function item_rating_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);

	// launch requests, asynchronously
	curl_batch($requests);
}

function item_rating_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('item_rating', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: item_rating_before_delete
	if(function_exists('item_rating_before_delete')) {
		$args = [];
		if(!item_rating_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	sql("DELETE FROM `item_rating` WHERE `ratingId`='{$selected_id}'", $eo);

	// hook: item_rating_after_delete
	if(function_exists('item_rating_after_delete')) {
		$args = [];
		item_rating_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='item_rating' AND `pkValue`='{$selected_id}'", $eo);
}

function item_rating_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('item_rating', $selected_id, 'edit')) return false;

	$data = [
		'rater_name' => Request::lookup('userId'),
	];

	// get existing values
	$old_data = getRecord('item_rating', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: item_rating_before_update
	if(function_exists('item_rating_before_update')) {
		$args = ['old_data' => $old_data];
		if(!item_rating_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'item_rating', 
		backtick_keys_once($set), 
		['`ratingId`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="item_rating_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	$eo = ['silentErrors' => true];

	update_calc_fields('item_rating', $data['selectedID'], calculated_fields()['item_rating']);

	// hook: item_rating_after_update
	if(function_exists('item_rating_after_update')) {
		$res = sql("SELECT * FROM `item_rating` WHERE `ratingId`='{$data['selectedID']}' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['ratingId'];
		$args = ['old_data' => $old_data];
		if(!item_rating_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update ownership data
	sql("UPDATE `membership_userrecords` SET `dateUpdated`='" . time() . "' WHERE `tableName`='item_rating' AND `pkValue`='" . makeSafe($selected_id) . "'", $eo);
}

