	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tuserId_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'item_rating_autofill.php?rnd1=$rnd1&mfk=userId&id=' + encodeURIComponent('".addslashes($row['userId'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'item_rating_autofill.php?rnd1=$rnd1&mfk=userId&id=' + encodeURIComponent(AppGini.current_userId{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#userId$rnd1').prop('disabled', true); \$j('#userIdLoading').html('<img src=loading.gif align=top>'); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm[1] || (($arrPerm[3] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm[3] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm[3] == 3)) ? "\$j('#userId$rnd1').prop('disabled', false); " : "\$j('#userId$rnd1').prop('disabled', true); ")."\$j('#userIdLoading').html(''); \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#userId_caption').length) \$j('#userId_caption').click(function() { userId_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	if( $_REQUEST['FilterField'][1]=='3' && $_REQUEST['FilterOperator'][1]=='<=>') {
		$templateCode.="\n<input type=hidden name=userId value=\"" . html_attr($_REQUEST['FilterValue'][1]) . "\">\n";
	}

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('item_rating');
	if($selected_id) {
		$jdata = get_joined_record('item_rating', $selected_id);
		if($jdata === false) $jdata = get_defaults('item_rating');
		$rdata = $row;
	}
	$templateCode .= loadView('item_rating-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: item_rating_dv
	if(function_exists('item_rating_dv')) {
		$args=[];
		item_rating_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}