function supplier_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('supplier', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: supplier_before_delete
	if(function_exists('supplier_before_delete')) {
		$args = [];
		if(!supplier_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: '' 
			);
	}

	// delete file stored in the 'identity_no' field
	$res = sql("SELECT `identity_no` FROM `supplier` WHERE `id`='{$selected_id}'", $eo);
	if($row = @db_fetch_row($res)) {
		if($row[0] != '') {
			@unlink(getUploadDir('') . $row[0]);
		}
	}

	// delete file stored in the 'licence_no' field
	$res = sql("SELECT `licence_no` FROM `supplier` WHERE `id`='{$selected_id}'", $eo);
	if($row = @db_fetch_row($res)) {
		if($row[0] != '') {
			@unlink(getUploadDir('') . $row[0]);
			$thumbDV = preg_replace('/\.(gif|png|jpg|jpeg|jpe)$/i', '_dv.$1', $row[0]);
			$thumbTV = preg_replace('/\.(gif|png|jpg|jpeg|jpe)$/i', '_tv.$1', $row[0]);
			@unlink(getUploadDir('') . $thumbTV);
			@unlink(getUploadDir('') . $thumbDV);
		}
	}

	sql("DELETE FROM `supplier` WHERE `id`='{$selected_id}'", $eo);

	// hook: supplier_after_delete
	if(function_exists('supplier_after_delete')) {
		$args = [];
		supplier_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='supplier' AND `pkValue`='{$selected_id}'", $eo);
}

