		$jdata = get_joined_record('supplier', $selected_id);
		if($jdata === false) $jdata = get_defaults('supplier');
		$rdata = $row;
	}
	$templateCode .= loadView('supplier-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: supplier_dv
	if(function_exists('supplier_dv')) {
		$args=[];
		supplier_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}